DROP TABLE IF EXISTS `symphony_comment`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `symphony_comment` (
  `oId` varchar(19) NOT NULL COMMENT '主键',
  `commentContent` text NOT NULL COMMENT '回帖正文内容',
  `commentCreateTime` bigint(20) NOT NULL COMMENT '回帖创建时间',
  `commentAuthorId` varchar(19) NOT NULL COMMENT '回帖作者 id',
  `commentOnArticleId` varchar(19) NOT NULL COMMENT '回帖的帖子 id',
  `commentSharpURL` varchar(255) NOT NULL COMMENT '回帖带锚点 URL',
  `commentOriginalCommentId` varchar(19) NOT NULL COMMENT '回复针对回帖 id，即 父回帖 id',
  `commentStatus` int(11) NOT NULL COMMENT '0：正常，1：封禁',
  `commentIP` varchar(128) NOT NULL COMMENT '回帖 IP',
  `commentUA` varchar(255) NOT NULL COMMENT 'User-Agent',
  `commentAnonymous` int(11) NOT NULL COMMENT '0：公开回帖，1：匿名回帖',
  `commentGoodCnt` int(11) NOT NULL COMMENT '回帖点赞计数',
  `commentBadCnt` int(11) NOT NULL COMMENT '回帖点踩计数',
  `commentScore` double NOT NULL COMMENT '回帖 Reddit 算法评分',
  `commentReplyCnt` int(11) NOT NULL COMMENT '回复计数',
  `commentAudioURL` varchar(255) NOT NULL COMMENT '回帖语音文件 URL',
  `commentQnAOffered` int(11) NOT NULL COMMENT '0：回帖未被采纳，1：回帖已被采纳（仅作用于问答帖）',
  `commentVisible` int(11) NOT NULL COMMENT '0：所有人可见，1：仅楼主和自己可见',
  PRIMARY KEY (`oId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='回帖表';
