CREATE TABLE `symphony_pointtransfer` (
  `oId` varchar(19) NOT NULL COMMENT '主键',
  `fromId` varchar(19) NOT NULL COMMENT '源账户 id',
  `toId` varchar(19) NOT NULL COMMENT '目标账户 id',
  `sum` int(11) NOT NULL COMMENT '转账金额',
  `fromBalance` int(11) NOT NULL COMMENT '源账户余额',
  `toBalance` int(11) NOT NULL COMMENT '目标账户余额',
  `time` bigint(20) NOT NULL COMMENT '转账时间',
  `type` int(11) NOT NULL COMMENT '转账类型',
  `dataId` varchar(19) NOT NULL COMMENT '数据实体 id',
  `memo` varchar(255) NOT NULL COMMENT '备注',
  PRIMARY KEY (`oId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='积分转账表';
