DROP TABLE IF EXISTS `symphony_tag`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `symphony_tag` (
  `oId` varchar(19) NOT NULL COMMENT '主键',
  `tagReferenceCount` int(11) NOT NULL COMMENT '标签引用（帖子/用户自评等）计数',
  `tagCommentCount` int(11) NOT NULL COMMENT '标签回帖计数',
  `tagFollowerCount` int(11) NOT NULL COMMENT '标签关注者计数',
  `tagLinkCount` int(11) NOT NULL COMMENT '标签链接引用计数',
  `tagTitle` varchar(50) NOT NULL COMMENT '标签标题',
  `tagURI` varchar(255) NOT NULL COMMENT '标签访问路径',
  `tagDescription` text NOT NULL COMMENT '标签描述',
  `tagIconPath` varchar(128) NOT NULL COMMENT '标签图标路径',
  `tagCSS` mediumtext NOT NULL COMMENT '标签 CSS',
  `tagStatus` int(11) NOT NULL COMMENT '0：正常，1：封禁',
  `tagGoodCnt` int(11) NOT NULL COMMENT '标签点赞计数',
  `tagBadCnt` int(11) NOT NULL COMMENT '标签点踩计数',
  `tagSeoTitle` varchar(128) NOT NULL COMMENT '标签 SEO 标题',
  `tagSeoKeywords` varchar(128) NOT NULL COMMENT '标签 SEO 关键字',
  `tagSeoDesc` text NOT NULL COMMENT '标签 SEO 描述',
  `tagRandomDouble` double NOT NULL COMMENT '标签随机数，用于快速选择随机标签',
  `tagAd` text NOT NULL COMMENT '标签广告',
  `tagShowSideAd` int(11) NOT NULL COMMENT '是否显示全站侧边栏广告',
  PRIMARY KEY (`oId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='标签表';
/*!40101 SET character_set_client = @saved_cs_client */;

